// Calculate how much time has elapsed
var elapsedTime = currentTime - startTime;

// Now, determine which "chapter" of the story we're in, based 
// on the number of milliseconds elapsed
// First ten seconds
if(elapsedTime < 10000) {
    msg.chapter = 1; 
}
// Between ten seconds and one minute
else if(elapsedTime < 60000) {
    msg.chapter = 2;
}
// Between one minute and 5 minutes
else if(elapsedTime < 300000) {
    msg.chapter = 3;
}
// Any time after 5 minutes
else {
    msg.chapter = 4;
}

