    ],
};

// Evaluates the type of value and returns it's type as a string
// Returns(non - string) undefined when value is undefined
const expression = [
    jsonata("$type(example[0])"),
    jsonata("$type(example[1])"),
    jsonata("$type(example[2])"),
    jsonata("$type(example[3])"),
    jsonata("$type(example[4])"),
    jsonata("$type(example[5])"),
    jsonata("$type(example[6])"),
    jsonata("$type(example[7])"),
];

msg.payload = [
    expression[0].evaluate(data),
    expression[1].evaluate(data),
