];

return msg;

// $sift(object, function)
// The function that is supplied must have the following signature:
// function(value [, key [, object]])

const data = {
    "Customer1": {
        "CustomerID": 12345,
        "Name": "Billy Bob",
        "Model": "Y",
    },
    "Employee1": {
