// Returns an array of the values return by the function
// when applied to each key/value pair in the object.
const expression = [
    jsonata("$each(function($v, $k) {$k & ': ' & $v})"),
    jsonata("$each(function($v, $k) {$k})"),
    jsonata("$each(function($v, $k) {$v})"),
];

msg.payload = [
    expression[0].evaluate(data),
    expression[1].evaluate(data),
    expression[2].evaluate(data),
];

return msg;

